import React, { useState, useEffect } from "react";
import { Container } from "@mui/material";
import Filter from "./components/Filter";
import AddContact from "./components/AddContact";
import Persons from "./components/Persons";
import apiService from "./services/helper";

const App = () => {
  const [contacts, setContacts] = useState([]);
  const [contact, setContact] = useState({
    fullName: "",
    phoneNumber: "",
  });
  const [filteredContacts, setFilteredContacts] = useState([]);

  useEffect(() => {
    apiService.getAll().then((returnedPerson) => {
      setPersons(returnedPerson);
    });
  }, []);

  const handleChangeName = (e) => {
    setNewName(e.target.value);
  };

  const handleChangeNum = (e) => {
    setNewNum(e.target.value);
  };

  const handleFilter = (e) => {
    setFilterName(e.target.value);
  };
  const filteredPersons =
    filterName === ""
      ? persons
      : persons &&
        persons.filter((person) =>
          person.name.toLowerCase().includes(filterName.toLowerCase())
        );
  const titleCase = (str) => {
    return str
      .toLowerCase()
      .split(" ")
      .map(function (word) {
        return word.replace(word[0], word[0].toUpperCase());
      })
      .join(" ");
  };
  //
  const addPerson = (e) => {
    e.preventDefault();
    const personObject = {
      name: titleCase(newName),
      number: newNumber,
    };
    apiService
      .create(personObject)
      .then((returnedPerson) => {
        setPersons(persons.concat(returnedPerson));
        setNewName("");
        setNewNum("");
      })
      .catch((error) => {
        let msg = Object.entries(error.response.data.errors);
        let errmsg = msg[0][1].message;
      });
  };

  const deletePerson = (details) => {
    if (window.confirm(`Delete ${details.name} ?`)) {
      apiService.remove(details.id).then((returnedPerson) => {
        setPersons(
          persons
            .map((person) =>
              person.id !== details.id ? person : returnedPerson
            )
            .filter((person) => Object.keys(person).length !== 0)
        );
      });
    }
  };

  return (
    <Container maxWidth="sm">
      <h1>Contacts</h1>
      <Filter filterName={filterName} handleFilter={handleFilter} />

      <AddContact
        title="Add Contact"
        addPerson={addPerson}
        newName={newName}
        newNumber={newNumber}
        handleChangeName={handleChangeName}
        handleChangeNum={handleChangeNum}
      />
      <br />
      <h2>Names~</h2>
      <Persons filteredPersons={filteredPersons} deletePerson={deletePerson} />
    </Container>
  );
};
export default App;
