import React, { useState, useEffect } from 'react'
import { Container } from '@mui/material'
import Filter from './components/Filter'
import AddContact from './components/AddContact'
import Contacts from './components/Contacts'
import apiService from './services/contacts'

export default App = () => {
    const [contacts, setContacts] = useState([])
    const [contact, setContact] = useState({
        fullName: '',
        phoneNumber: '',
    })
    const [searchValue, setSearchValue] = useState('')

    useEffect(() => {
        apiService.getAll().then((contacts) => {
            setContacts(contacts);
        })
    }, [])

    const handleChangeContact = e => {
        const { name, value } = e.name
        setContact({ ...contact, [name]: value })
    }

    const addPerson = (e) => {
        e.preventDefault();
        const personObject = {
        name: titleCase(newName),
        number: newNumber,
        };
        apiService
        .create(personObject)
        .then((returnedPerson) => {
            setPersons(persons.concat(returnedPerson));
            setNewName("");
            setNewNum("");
        })
        .catch((error) => {
            let msg = Object.entries(error.response.data.errors);
            let errmsg = msg[0][1].message;
        });
    };

    const deletePerson = (details) => {
        if (window.confirm(`Delete ${details.name} ?`)) {
        apiService.remove(details.id).then((returnedPerson) => {
            setPersons(
            persons
                .map((person) =>
                person.id !== details.id ? person : returnedPerson
                )
                .filter((person) => Object.keys(person).length !== 0)
            );
        });
        }
    };

    return (
        <Container maxWidth="sm">
            <h1>Contacts</h1>
            <Filter searchValue={searchValue} handleFilter={handleFilter} />
            <AddContact
                title="Add Contact"
                contact={contact}
                handleChangeContact={handleChangeContact}
                addPerson={addPerson}
            />
            <Contacts filteredPersons={filteredPersons} deletePerson={deletePerson} />
        </Container>
    )
}
